import { useState } from 'react';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from '@/components/ui/select';
import { Play, Loader2 } from 'lucide-react';
import { useWorkflows } from '@/hooks/useWorkflows';
import { useGitOperations } from '@/hooks/useGitOperations';
import { useToast } from '@/hooks/use-toast';

interface WorkflowTriggerProps {
    owner: string;
    repo: string;
    workflowId: number;
    workflowName: string;
}

export function WorkflowTrigger({
    owner,
    repo,
    workflowId,
    workflowName,
}: WorkflowTriggerProps) {
    const [open, setOpen] = useState(false);
    const [ref, setRef] = useState('main'); // Default branch
    const [inputs, setInputs] = useState(''); // JSON string for inputs
    const { toast } = useToast();

    const { triggerWorkflow, isTriggeringWorkflow } = useWorkflows({ owner, repo });
    const { branches } = useGitOperations({ owner, repo });

    const handleTrigger = () => {
        let parsedInputs: Record<string, any> | undefined;

        if (inputs.trim()) {
            try {
                parsedInputs = JSON.parse(inputs);
            } catch (e) {
                toast({
                    title: 'Invalid JSON',
                    description: 'Please enter valid JSON for inputs',
                    variant: 'destructive',
                });
                return;
            }
        }

        triggerWorkflow(
            {
                workflowId,
                ref,
                inputs: parsedInputs,
            },
            {
                onSuccess: () => {
                    setOpen(false);
                    setInputs('');
                },
            }
        );
    };

    return (
        <Dialog open={open} onOpenChange={setOpen}>
            <DialogTrigger asChild>
                <Button variant="outline" size="sm">
                    <Play className="h-3 w-3 mr-2" />
                    Run Workflow
                </Button>
            </DialogTrigger>
            <DialogContent className="sm:max-w-[425px]">
                <DialogHeader>
                    <DialogTitle>Run Workflow: {workflowName}</DialogTitle>
                    <DialogDescription>
                        Manually trigger this workflow. Specify the branch and any required inputs.
                    </DialogDescription>
                </DialogHeader>
                <div className="grid gap-4 py-4">
                    <div className="grid grid-cols-4 items-center gap-4">
                        <Label htmlFor="branch" className="text-right">
                            Branch
                        </Label>
                        <div className="col-span-3">
                            <Select value={ref} onValueChange={setRef}>
                                <SelectTrigger>
                                    <SelectValue placeholder="Select branch" />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="main">main</SelectItem>
                                    {branches?.map((branch) => (
                                        <SelectItem key={branch.name} value={branch.name}>
                                            {branch.name}
                                        </SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>
                    </div>
                    <div className="grid grid-cols-4 items-start gap-4">
                        <Label htmlFor="inputs" className="text-right mt-2">
                            Inputs (JSON)
                        </Label>
                        <Textarea
                            id="inputs"
                            placeholder='{"environment": "production"}'
                            className="col-span-3 font-mono text-xs"
                            value={inputs}
                            onChange={(e) => setInputs(e.target.value)}
                            rows={5}
                        />
                    </div>
                </div>
                <DialogFooter>
                    <Button variant="outline" onClick={() => setOpen(false)}>
                        Cancel
                    </Button>
                    <Button onClick={handleTrigger} disabled={isTriggeringWorkflow}>
                        {isTriggeringWorkflow && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                        Run Workflow
                    </Button>
                </DialogFooter>
            </DialogContent>
        </Dialog>
    );
}

/**
 * WorkflowsDashboard Component - Main GitHub Actions interface
 * 
 * Features:
 * - List all workflows in a repository
 * - View workflow status and last run
 * - Trigger workflows manually
 * - Enable/disable workflows
 */

import { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useWorkflows, useWorkflowRuns, useTriggerWorkflow, useEnableWorkflow, useDisableWorkflow } from '@/hooks/github/useActionsOperations';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Skeleton } from '@/components/ui/skeleton';
import { Switch } from '@/components/ui/switch';
import { Label } from '@/components/ui/label';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogFooter,
    DialogHeader,
    DialogTitle,
} from '@/components/ui/dialog';
import {
    Play,
    RefreshCw,
    CheckCircle2,
    XCircle,
    Clock,
    ArrowLeft,
    FileCode,
    GitBranch,
} from 'lucide-react';
import { toast } from 'sonner';
import type { GitHubWorkflow } from '@/types/github';
import { formatDistanceToNow, parseISO } from 'date-fns';

export function WorkflowsDashboard() {
    const { owner, repo } = useParams<{ owner: string; repo: string }>();
    const navigate = useNavigate();
    const [triggerDialogOpen, setTriggerDialogOpen] = useState(false);
    const [selectedWorkflow, setSelectedWorkflow] = useState<GitHubWorkflow | null>(null);

    const { data: workflows, isLoading, error, refetch } = useWorkflows(owner || '', repo || '');
    const { data: recentRuns } = useWorkflowRuns(owner || '', repo || '', undefined, { per_page: 100 });
    const triggerWorkflow = useTriggerWorkflow();
    const enableWorkflow = useEnableWorkflow();
    const disableWorkflow = useDisableWorkflow();

    // Get last run for a workflow
    const getLastRun = (workflowId: number) => {
        return recentRuns?.find(run => run.workflow_id === workflowId);
    };

    const handleTrigger = (workflow: GitHubWorkflow) => {
        setSelectedWorkflow(workflow);
        setTriggerDialogOpen(true);
    };

    const confirmTrigger = async () => {
        if (!selectedWorkflow || !owner || !repo) return;

        try {
            await triggerWorkflow.mutateAsync({
                owner,
                repo,
                workflowId: selectedWorkflow.id,
                ref: 'main', // Default to main branch
            });
            setTriggerDialogOpen(false);
            setSelectedWorkflow(null);
        } catch (error) {
            // Error handled by hook
        }
    };

    const handleToggleWorkflow = async (workflow: GitHubWorkflow, enabled: boolean) => {
        if (!owner || !repo) return;

        try {
            if (enabled) {
                await enableWorkflow.mutateAsync({ owner, repo, workflowId: workflow.id });
            } else {
                await disableWorkflow.mutateAsync({ owner, repo, workflowId: workflow.id });
            }
        } catch (error) {
            // Error handled by hook
        }
    };

    if (!owner || !repo) {
        return (
            <div className="container mx-auto py-6">
                <p className="text-destructive">Invalid repository parameters</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="container mx-auto py-6">
                <div className="flex flex-col items-center justify-center h-96 space-y-4">
                    <p className="text-destructive">Failed to load workflows: {(error as Error).message}</p>
                    <Button onClick={() => refetch()} variant="outline">
                        <RefreshCw className="mr-2 h-4 w-4" />
                        Retry
                    </Button>
                </div>
            </div>
        );
    }

    return (
        <div className="container mx-auto py-6 space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
                <div>
                    <div className="flex items-center gap-2">
                        <Button variant="ghost" size="icon" onClick={() => navigate('/github/repositories')}>
                            <ArrowLeft className="h-4 w-4" />
                        </Button>
                        <div>
                            <h1 className="text-3xl font-bold">GitHub Actions</h1>
                            <p className="text-muted-foreground">
                                {owner}/{repo} ΓÇó {workflows?.length || 0} workflows
                            </p>
                        </div>
                    </div>
                </div>
                <Button onClick={() => refetch()} variant="outline" disabled={isLoading}>
                    <RefreshCw className={`mr-2 h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
                    Refresh
                </Button>
            </div>

            {/* Workflows Grid */}
            {isLoading ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {Array.from({ length: 6 }).map((_, i) => (
                        <Card key={i}>
                            <CardHeader>
                                <Skeleton className="h-6 w-3/4" />
                                <Skeleton className="h-4 w-full" />
                            </CardHeader>
                            <CardContent>
                                <Skeleton className="h-20 w-full" />
                            </CardContent>
                        </Card>
                    ))}
                </div>
            ) : workflows && workflows.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {workflows.map((workflow) => (
                        <WorkflowCard
                            key={workflow.id}
                            workflow={workflow}
                            lastRun={getLastRun(workflow.id)}
                            onTrigger={() => handleTrigger(workflow)}
                            onViewRuns={() => navigate(`/github/actions/${owner}/${repo}/workflows/${workflow.id}`)}
                            onToggle={(enabled) => handleToggleWorkflow(workflow, enabled)}
                        />
                    ))}
                </div>
            ) : (
                <div className="text-center py-12">
                    <FileCode className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
                    <p className="text-muted-foreground">No workflows found in this repository</p>
                </div>
            )}

            {/* Trigger Confirmation Dialog */}
            <Dialog open={triggerDialogOpen} onOpenChange={setTriggerDialogOpen}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle>Trigger Workflow</DialogTitle>
                        <DialogDescription>
                            Are you sure you want to manually trigger "{selectedWorkflow?.name}"?
                        </DialogDescription>
                    </DialogHeader>
                    <DialogFooter>
                        <Button variant="outline" onClick={() => setTriggerDialogOpen(false)}>
                            Cancel
                        </Button>
                        <Button onClick={confirmTrigger} disabled={triggerWorkflow.isPending}>
                            {triggerWorkflow.isPending ? 'Triggering...' : 'Trigger Workflow'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>
        </div>
    );
}

interface WorkflowCardProps {
    workflow: GitHubWorkflow;
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    lastRun?: { status: string; conclusion: string | null; updated_at: string } | undefined;
    onTrigger: () => void;
    onViewRuns: () => void;
    onToggle: (enabled: boolean) => void;
}

function WorkflowCard({ workflow, lastRun, onTrigger, onViewRuns, onToggle }: WorkflowCardProps) {
    const getStatusIcon = () => {
        if (!lastRun) return <Clock className="h-4 w-4 text-muted-foreground" />;
        if (lastRun.status === 'in_progress' || lastRun.status === 'queued') {
            return <RefreshCw className="h-4 w-4 text-blue-500 animate-spin" />;
        }
        if (lastRun.conclusion === 'success') {
            return <CheckCircle2 className="h-4 w-4 text-green-500" />;
        }
        if (lastRun.conclusion === 'failure') {
            return <XCircle className="h-4 w-4 text-red-500" />;
        }
        return <Clock className="h-4 w-4 text-muted-foreground" />;
    };

    const getStatusText = () => {
        if (!lastRun) return 'Never run';
        if (lastRun.status === 'in_progress') return 'Running';
        if (lastRun.status === 'queued') return 'Queued';
        if (lastRun.conclusion === 'success') return 'Success';
        if (lastRun.conclusion === 'failure') return 'Failed';
        if (lastRun.conclusion === 'cancelled') return 'Cancelled';
        return 'Unknown';
    };

    return (
        <Card className="hover:shadow-lg transition-shadow">
            <CardHeader>
                <div className="flex items-start justify-between">
                    <div className="flex-1 min-w-0">
                        <CardTitle className="text-lg truncate">{workflow.name}</CardTitle>
                        <CardDescription className="flex items-center gap-2 mt-1">
                            <GitBranch className="h-3 w-3" />
                            <span className="truncate">{workflow.path}</span>
                        </CardDescription>
                    </div>
                </div>
            </CardHeader>
            <CardContent className="space-y-4">
                {/* Last Run Status */}
                <div className="flex items-center justify-between p-3 bg-muted/50 rounded-md">
                    <div className="flex items-center gap-2">
                        {getStatusIcon()}
                        <div>
                            <p className="text-sm font-medium">{getStatusText()}</p>
                            {lastRun && (
                                <p className="text-xs text-muted-foreground">
                                    {formatDistanceToNow(parseISO(lastRun.updated_at))} ago
                                </p>
                            )}
                        </div>
                    </div>
                </div>

                {/* State badge */}
                <div className="flex items-center gap-2">
                    <Badge variant={workflow.state === 'active' ? 'default' : 'secondary'}>
                        {workflow.state}
                    </Badge>
                </div>

                {/* Actions */}
                <div className="grid grid-cols-2 gap-2">
                    <Button size="sm" variant="outline" onClick={onTrigger}>
                        <Play className="mr-2 h-4 w-4" />
                        Run
                    </Button>
                    <Button size="sm" variant="outline" onClick={onViewRuns}>
                        View Runs
                    </Button>
                </div>

                {/* Enable/Disable Toggle */}
                <div className="flex items-center space-x-2">
                    <Switch
                        id={`workflow-${workflow.id}`}
                        checked={workflow.state === 'active'}
                        onCheckedChange={onToggle}
                    />
                    <Label htmlFor={`workflow-${workflow.id}`} className="text-sm">
                        {workflow.state === 'active' ? 'Enabled' : 'Disabled'}
                    </Label>
                </div>
            </CardContent>
        </Card>
    );
}

/**
 * RepositoryBackupExplorer Component
 * 
 * Explore repository files and trigger backups
 */

import { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useRepositoryTree } from '@/hooks/github/useGitOperations';
import { useCreateBackup } from '@/hooks/github/useBackupOperations';
import { useRepository } from '@/hooks/github/useGitOperations';
import { FileTree, buildFileTree, FileNode } from '@/components/github/editor/FileTree';
import { Card, CardContent, CardDescription, CardHeader, CardTitle, CardFooter } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { ArrowLeft, Download, Loader2, GitBranch, Archive } from 'lucide-react';
import { format } from 'date-fns';

export function RepositoryBackupExplorer() {
    const { owner, repo } = useParams<{ owner: string; repo: string }>();
    const navigate = useNavigate();
    const [backupProgress, setBackupProgress] = useState(0);
    const [currentFile, setCurrentFile] = useState('');

    // Default branch 'main' for now, ideally fetch default branch
    const { data: repository } = useRepository(owner!, repo!);
    const defaultBranch = repository?.default_branch || 'main';

    const { data: tree, isLoading: isLoadingTree } = useRepositoryTree(owner!, repo!, defaultBranch);
    const createBackup = useCreateBackup();

    const fileTree = tree ? buildFileTree(tree as any[]) : [];

    const handleBackup = () => {
        if (!owner || !repo) return;

        setBackupProgress(0);
        createBackup.mutate({
            owner,
            repo,
            branch: defaultBranch,
            onProgress: (progress, file) => {
                setBackupProgress(progress);
                setCurrentFile(file);
            }
        });
    };

    const handleFileSelect = (node: FileNode) => {
        // Maybe preview file? For now, do nothing or show info
        console.log('Selected:', node.path);
    };

    if (!owner || !repo) return <div className="p-10 text-center">Invalid Repository URL</div>;

    return (
        <div className="container mx-auto py-6 space-y-6 h-[cal(100vh-4rem)] flex flex-col">
            <div className="flex items-center gap-4">
                <Button variant="ghost" size="icon" onClick={() => navigate('/github/backup')}>
                    <ArrowLeft className="h-4 w-4" />
                </Button>
                <div>
                    <h1 className="text-2xl font-bold flex items-center gap-2">
                        <Archive className="h-6 w-6" />
                        {owner}/{repo}
                    </h1>
                    <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
                        <GitBranch className="h-3 w-3" />
                        <span>{defaultBranch}</span>
                        {tree && <Badge variant="outline" className="ml-2">{tree.length} files</Badge>}
                    </div>
                </div>
                <div className="ml-auto">
                    <Button onClick={handleBackup} disabled={createBackup.isPending || isLoadingTree}>
                        {createBackup.isPending ? (
                            <>
                                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                Backing Up...
                            </>
                        ) : (
                            <>
                                <Download className="mr-2 h-4 w-4" />
                                Download Backup
                            </>
                        )}
                    </Button>
                </div>
            </div>

            {/* Progress Bar */}
            {createBackup.isPending && (
                <Card className="border-blue-200 bg-blue-50 dark:bg-blue-950/20 dark:border-blue-900">
                    <CardContent className="pt-6">
                        <div className="mb-2 flex justify-between text-sm">
                            <span className="font-medium text-blue-700 dark:text-blue-300">Creating ZIP Archive...</span>
                            <span className="text-blue-700 dark:text-blue-300">{backupProgress}%</span>
                        </div>
                        <Progress value={backupProgress} className="h-2" />
                        <p className="text-xs text-muted-foreground mt-2 truncate">
                            Processing: {currentFile}
                        </p>
                    </CardContent>
                </Card>
            )}

            {/* File Explorer */}
            <Card className="flex-1 flex flex-col min-h-0">
                <CardHeader className="py-3 border-b">
                    <CardTitle className="text-sm font-medium">Repository Explorer</CardTitle>
                </CardHeader>
                <CardContent className="flex-1 p-0 overflow-hidden relative">
                    {isLoadingTree ? (
                        <div className="absolute inset-0 flex items-center justify-center bg-background/50 z-10">
                            <div className="flex flex-col items-center gap-2">
                                <Loader2 className="h-8 w-8 animate-spin text-primary" />
                                <p className="text-sm text-muted-foreground">Fetching file list...</p>
                            </div>
                        </div>
                    ) : (
                        <FileTree
                            files={fileTree}
                            onFileSelect={handleFileSelect}
                        />
                    )}
                </CardContent>
                <CardFooter className="py-2 border-t text-xs text-muted-foreground bg-muted/20">
                    Files are fetched directly from GitHub API. Large repositories may take time.
                </CardFooter>
            </Card>
        </div>
    );
}

/**
 * CodeEditor Component - Monaco Editor Integration
 * 
 * Features:
 * - Syntax highlighting for all languages
 * - IntelliSense and autocomplete
 * - Keyboard shortcuts (Cmd+S, Cmd+F, etc.)
 * - Theme support (light/dark)
 * - Diff view support
 */

import { useEffect, useRef, useState } from 'react';
import Editor, { OnMount, Monaco } from '@monaco-editor/react';
import { useTheme } from "@/hooks/use-theme";
import { Skeleton } from '@/components/ui/skeleton';
import type { editor } from 'monaco-editor';

interface CodeEditorProps {
    value: string;
    language?: string;
    onChange?: (value: string | undefined) => void;
    onSave?: (value: string) => void;
    readOnly?: boolean;
    height?: string;
    options?: editor.IStandaloneEditorConstructionOptions;
}

export function CodeEditor({
    value,
    language = 'javascript',
    onChange,
    onSave,
    readOnly = false,
    height = '100%',
    options = {},
}: CodeEditorProps) {
    const { theme } = useTheme();
    const editorRef = useRef<editor.IStandaloneCodeEditor | null>(null);
    const monacoRef = useRef<Monaco | null>(null);
    const [isReady, setIsReady] = useState(false);

    const handleEditorDidMount: OnMount = (editor, monaco) => {
        editorRef.current = editor;
        monacoRef.current = monaco;
        setIsReady(true);

        // Add keyboard shortcuts
        editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyS, () => {
            if (onSave) {
                const currentValue = editor.getValue();
                onSave(currentValue);
            }
        });

        // Focus editor
        editor.focus();
    };

    const handleChange = (value: string | undefined) => {
        if (onChange) {
            onChange(value);
        }
    };

    // Configure editor options
    const editorOptions: editor.IStandaloneEditorConstructionOptions = {
        minimap: { enabled: true },
        fontSize: 14,
        lineNumbers: 'on',
        roundedSelection: true,
        scrollBeyondLastLine: false,
        readOnly,
        automaticLayout: true,
        tabSize: 2,
        wordWrap: 'on',
        formatOnPaste: true,
        formatOnType: true,
        suggestOnTriggerCharacters: true,
        acceptSuggestionOnEnter: 'on',
        quickSuggestions: true,
        parameterHints: { enabled: true },
        ...options,
    };

    return (
        <div className="w-full h-full relative">
            <Editor
                height={height}
                language={language}
                value={value}
                onChange={handleChange}
                onMount={handleEditorDidMount}
                theme={theme === 'dark' ? 'vs-dark' : 'light'}
                options={editorOptions}
                loading={
                    <div className="w-full h-full p-4">
                        <Skeleton className="w-full h-8 mb-2" />
                        <Skeleton className="w-3/4 h-8 mb-2" />
                        <Skeleton className="w-full h-8 mb-2" />
                        <Skeleton className="w-2/3 h-8 mb-2" />
                        <Skeleton className="w-full h-8 mb-2" />
                    </div>
                }
            />
        </div>
    );
}

// Language detection from file extension
export function getLanguageFromFilename(filename: string): string {
    const ext = filename.split('.').pop()?.toLowerCase();

    const languageMap: Record<string, string> = {
        // Web
        js: 'javascript',
        jsx: 'javascript',
        ts: 'typescript',
        tsx: 'typescript',
        html: 'html',
        css: 'css',
        scss: 'scss',
        less: 'less',
        json: 'json',

        // Backend
        py: 'python',
        java: 'java',
        go: 'go',
        rs: 'rust',
        php: 'php',
        rb: 'ruby',

        // Shell
        sh: 'shell',
        bash: 'shell',
        zsh: 'shell',

        // Config
        yaml: 'yaml',
        yml: 'yaml',
        toml: 'toml',
        xml: 'xml',

        // Docs
        md: 'markdown',
        txt: 'plaintext',

        // Database
        sql: 'sql',

        // Others
        c: 'c',
        cpp: 'cpp',
        cs: 'csharp',
        swift: 'swift',
        kt: 'kotlin',
        dart: 'dart',
    };

    return languageMap[ext || ''] || 'plaintext';
}

/**
 * FileTree Component - Repository file browser
 * 
 * Browse and select files from a repository
 */

import { useState } from 'react';
import { ChevronRight, ChevronDown, File, Folder, FolderOpen } from 'lucide-react';
import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';

export interface FileNode {
    path: string;
    name: string;
    type: 'file' | 'dir';
    children?: FileNode[];
    size?: number;
}

interface FileTreeProps {
    files: FileNode[];
    onFileSelect: (file: FileNode) => void;
    selectedPath?: string;
}

export function FileTree({ files, onFileSelect, selectedPath }: FileTreeProps) {
    return (
        <ScrollArea className="h-full">
            <div className="p-2">
                {files.map((node) => (
                    <FileTreeNode
                        key={node.path}
                        node={node}
                        level={0}
                        onSelect={onFileSelect}
                        selectedPath={selectedPath}
                    />
                ))}
            </div>
        </ScrollArea>
    );
}

interface FileTreeNodeProps {
    node: FileNode;
    level: number;
    onSelect: (file: FileNode) => void;
    selectedPath?: string;
}

function FileTreeNode({ node, level, onSelect, selectedPath }: FileTreeNodeProps) {
    const [isExpanded, setIsExpanded] = useState(level === 0);
    const isSelected = selectedPath === node.path;
    const isDirectory = node.type === 'dir';

    const handleClick = () => {
        if (isDirectory) {
            setIsExpanded(!isExpanded);
        } else {
            onSelect(node);
        }
    };

    return (
        <div>
            <Button
                variant="ghost"
                className={cn(
                    'w-full justify-start px-2 py-1 h-auto font-normal hover:bg-accent',
                    isSelected && 'bg-accent'
                )}
                style={{ paddingLeft: `${level * 12 + 8}px` }}
                onClick={handleClick}
            >
                {isDirectory ? (
                    <>
                        {isExpanded ? (
                            <ChevronDown className="h-4 w-4 mr-1 shrink-0" />
                        ) : (
                            <ChevronRight className="h-4 w-4 mr-1 shrink-0" />
                        )}
                        {isExpanded ? (
                            <FolderOpen className="h-4 w-4 mr-2 shrink-0 text-blue-500" />
                        ) : (
                            <Folder className="h-4 w-4 mr-2 shrink-0 text-blue-500" />
                        )}
                    </>
                ) : (
                    <>
                        <span className="w-4 mr-1" /> {/* Spacer for alignment */}
                        <File className="h-4 w-4 mr-2 shrink-0 text-muted-foreground" />
                    </>
                )}
                <span className="truncate text-sm">{node.name}</span>
            </Button>

            {isDirectory && isExpanded && node.children && (
                <div>
                    {node.children.map((child) => (
                        <FileTreeNode
                            key={child.path}
                            node={child}
                            level={level + 1}
                            onSelect={onSelect}
                            selectedPath={selectedPath}
                        />
                    ))}
                </div>
            )}
        </div>
    );
}

/**
 * Convert flat GitHub tree structure to nested file tree
 */
export function buildFileTree(files: Array<{ path: string; type: 'blob' | 'tree' }>): FileNode[] {
    const root: FileNode[] = [];
    const pathMap = new Map<string, FileNode>();

    // Sort files by path to ensure parents come before children
    const sortedFiles = [...files].sort((a, b) => a.path.localeCompare(b.path));

    for (const file of sortedFiles) {
        const parts = file.path.split('/');
        const name = parts[parts.length - 1];

        const node: FileNode = {
            path: file.path,
            name,
            type: file.type === 'tree' ? 'dir' : 'file',
            children: file.type === 'tree' ? [] : undefined,
        };

        pathMap.set(file.path, node);

        if (parts.length === 1) {
            // Root level
            root.push(node);
        } else {
            // Nested - find parent
            const parentPath = parts.slice(0, -1).join('/');
            const parent = pathMap.get(parentPath);

            if (parent && parent.type === 'dir') {
                parent.children = parent.children || [];
                parent.children.push(node);
            } else {
                // Parent doesn't exist yet, create placeholder directories
                let currentPath = '';
                for (let i = 0; i < parts.length - 1; i++) {
                    const part = parts[i];
                    const newPath = currentPath ? `${currentPath}/${part}` : part;

                    if (!pathMap.has(newPath)) {
                        const dirNode: FileNode = {
                            path: newPath,
                            name: part,
                            type: 'dir',
                            children: [],
                        };

                        pathMap.set(newPath, dirNode);

                        if (i === 0) {
                            root.push(dirNode);
                        } else {
                            const parentDir = pathMap.get(currentPath);
                            if (parentDir && parentDir.children) {
                                parentDir.children.push(dirNode);
                            }
                        }
                    }

                    currentPath = newPath;
                }

                // Now add the file to its parent
                const finalParent = pathMap.get(parts.slice(0, -1).join('/'));
                if (finalParent && finalParent.children) {
                    finalParent.children.push(node);
                }
            }
        }
    }

    return root;
}

import { useState } from 'react';
import { Calendar as CalendarIcon, ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Calendar } from '@/components/ui/calendar';
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/components/ui/popover';
import { format, subDays, addDays } from 'date-fns';

interface DailyNotesProps {
    onSelectDate: (date: Date) => void;
    currentDate?: Date;
}

export function DailyNotes({ onSelectDate, currentDate = new Date() }: DailyNotesProps) {
    const [date, setDate] = useState<Date>(currentDate);

    const handleDateSelect = (newDate: Date | undefined) => {
        if (newDate) {
            setDate(newDate);
            onSelectDate(newDate);
        }
    };

    const goToPrevDay = () => {
        const prev = subDays(date, 1);
        setDate(prev);
        onSelectDate(prev);
    };

    const goToNextDay = () => {
        const next = addDays(date, 1);
        setDate(next);
        onSelectDate(next);
    };

    const goToToday = () => {
        const today = new Date();
        setDate(today);
        onSelectDate(today);
    };

    const isToday = format(date, 'yyyy-MM-dd') === format(new Date(), 'yyyy-MM-dd');

    return (
        <div className="flex items-center gap-2">
            <Button
                variant="outline"
                size="icon"
                onClick={goToPrevDay}
                title="Previous day"
            >
                <ChevronLeft className="h-4 w-4" />
            </Button>

            <Popover>
                <PopoverTrigger asChild>
                    <Button variant="outline" className="min-w-[200px] justify-start">
                        <CalendarIcon className="mr-2 h-4 w-4" />
                        {format(date, 'EEEE, MMM d, yyyy')}
                    </Button>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0" align="start">
                    <Calendar
                        mode="single"
                        selected={date}
                        onSelect={handleDateSelect}
                        initialFocus
                    />
                </PopoverContent>
            </Popover>

            <Button
                variant="outline"
                size="icon"
                onClick={goToNextDay}
                title="Next day"
            >
                <ChevronRight className="h-4 w-4" />
            </Button>

            {!isToday && (
                <Button variant="ghost" onClick={goToToday}>
                    Today
                </Button>
            )}
        </div>
    );
}

/**
 * Generate daily note title from date
 */
export function getDailyNoteTitle(date: Date): string {
    return format(date, 'yyyy-MM-dd');
}

/**
