import { createContext, useContext, useEffect, useState, ReactNode } from "react";
import { User, Session } from "@supabase/supabase-js";
import { supabase } from "@/integrations/supabase/client";

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  signUp: (email: string, password: string, displayName?: string) => Promise<{ error: Error | null }>;
  signIn: (email: string, password: string) => Promise<{ error: Error | null }>;
  signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Get initial session
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session);
      setUser(session?.user ?? null);
      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signUp = async (email: string, password: string, displayName?: string) => {
    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: { display_name: displayName },
      },
    });
    return { error: error as Error | null };
  };

  const signIn = async (email: string, password: string) => {
    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });
    return { error: error as Error | null };
  };

  const signOut = async () => {
    await supabase.auth.signOut();
  };

  return (
    <AuthContext.Provider value={{ user, session, loading, signUp, signIn, signOut }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

import { createContext, useContext, useState, useEffect, ReactNode } from "react";
import { CommandPalette } from "@/components/notes/CommandPalette";

interface CommandPaletteContextType {
  isOpen: boolean;
  setIsOpen: (open: boolean) => void;
}

const CommandPaletteContext = createContext<CommandPaletteContextType | undefined>(undefined);

export function CommandPaletteProvider({ children }: { children: ReactNode }) {
  const [isOpen, setIsOpen] = useState(false);

  // Global keyboard shortcut for Cmd+K / Ctrl+K
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if ((e.metaKey || e.ctrlKey) && e.key === "k") {
        e.preventDefault();
        setIsOpen((prev) => !prev);
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, []);

  return (
    <CommandPaletteContext.Provider value={{ isOpen, setIsOpen }}>
      {children}
      <CommandPalette open={isOpen} onOpenChange={setIsOpen} />
    </CommandPaletteContext.Provider>
  );
}

export function useCommandPalette() {
  const context = useContext(CommandPaletteContext);
  if (context === undefined) {
    throw new Error("useCommandPalette must be used within a CommandPaletteProvider");
  }
  return context;
}

import { useState, useCallback, useEffect, useRef } from 'react';
import { SlashCommand, BlockType } from '@/types/editor.types';
import { BLOCK_TEMPLATES } from '@/data/templates';
import {
    Type,
    Heading1,
    Heading2,
    Heading3,
    FileText,
    List,
    ListOrdered,
    CheckSquare,
    ChevronRight,
    Code,
    Quote,
    AlertCircle,
    Calculator,
    Link2,
    ChevronDown,
    Columns2,
    Columns3,
    FileStack,
    Network,
} from 'lucide-react';

interface UseSlashCommandOptions {
    onCommand: (command: SlashCommand, blockId: string) => void;
    triggerChar?: string;
}

export const useSlashCommand = ({
    onCommand,
    triggerChar = '/',
}: UseSlashCommandOptions) => {
    const [isOpen, setIsOpen] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [selectedIndex, setSelectedIndex] = useState(0);
    const [position, setPosition] = useState({ x: 0, y: 0 });
    const [currentBlockId, setCurrentBlockId] = useState<string | null>(null);
    const menuRef = useRef<HTMLDivElement>(null);

    // Get all available slash commands
    const getCommands = useCallback((blockId: string): SlashCommand[] => {
        return [
            // Basic text
            {
                id: 'text',
                label: 'Text',
                description: 'Just start writing with plain text',
                icon: 'Type',
                keywords: ['text', 'paragraph', 'p'],
                category: 'basic',
                action: () => onCommand({ id: 'text' } as SlashCommand, blockId),
            },
            // Headings
            {
                id: 'heading1',
                label: 'Heading 1',
                description: 'Big section heading',
                icon: 'Heading1',
                keywords: ['heading', 'h1', 'title'],
                category: 'text',
                action: () => onCommand({ id: 'heading1' } as SlashCommand, blockId),
            },
            {
                id: 'heading2',
                label: 'Heading 2',
                description: 'Medium section heading',
                icon: 'Heading2',
                keywords: ['heading', 'h2', 'subtitle'],
                category: 'text',
                action: () => onCommand({ id: 'heading2' } as SlashCommand, blockId),
            },
            {
                id: 'heading3',
                label: 'Heading 3',
                description: 'Small section heading',
                icon: 'Heading3',
                keywords: ['heading', 'h3'],
                category: 'text',
                action: () => onCommand({ id: 'heading3' } as SlashCommand, blockId),
            },
            // Page
            {
                id: 'page',
                label: 'Page',
                description: 'Embed a sub-page inside this page',
                icon: 'FileText',
                keywords: ['page', 'subpage', 'embed'],
                category: 'basic',
                action: () => onCommand({ id: 'page' } as SlashCommand, blockId),
            },
            // Lists
            {
                id: 'bulleted-list',
                label: 'Bulleted list',
                description: 'Create a simple bulleted list',
                icon: 'List',
                keywords: ['list', 'bullet', 'ul'],
                category: 'list',
                action: () => onCommand({ id: 'bulleted-list' } as SlashCommand, blockId),
            },
            {
                id: 'numbered-list',
                label: 'Numbered list',
                description: 'Create a list with numbering',
                icon: 'ListOrdered',
                keywords: ['list', 'number', 'ol', 'ordered'],
                category: 'list',
                action: () => onCommand({ id: 'numbered-list' } as SlashCommand, blockId),
            },
            {
                id: 'todo',
                label: 'To-do list',
                description: 'Track tasks with a to-do list',
                icon: 'CheckSquare',
                keywords: ['todo', 'task', 'checkbox', 'check'],
                category: 'list',
                action: () => onCommand({ id: 'todo' } as SlashCommand, blockId),
            },
            {
                id: 'toggle',
                label: 'Toggle list',
                description: 'Toggles can hide and show content inside',
                icon: 'ChevronRight',
                keywords: ['toggle', 'collapse', 'accordion'],
                category: 'list',
                action: () => onCommand({ id: 'toggle' } as SlashCommand, blockId),
            },
            // Media
            {
                id: 'code',
                label: 'Code',
                description: 'Capture a code snippet',
                icon: 'Code',
                keywords: ['code', 'snippet', 'programming'],
                category: 'media',
                action: () => onCommand({ id: 'code' } as SlashCommand, blockId),
            },
            {
                id: 'quote',
                label: 'Quote',
                description: 'Capture a quote',
                icon: 'Quote',
                keywords: ['quote', 'blockquote', 'citation'],
                category: 'media',
                action: () => onCommand({ id: 'quote' } as SlashCommand, blockId),
            },
            {
                id: 'callout',
                label: 'Callout',
                description: 'Make writing stand out',
                icon: 'AlertCircle',
                keywords: ['callout', 'alert', 'note', 'info'],
                category: 'media',
                action: () => onCommand({ id: 'callout' } as SlashCommand, blockId),
            },
            // Advanced
            {
                id: 'equation',
                label: 'Block equation',
                description: 'Display a standalone math equation',
                icon: 'Calculator',
                keywords: ['math', 'equation', 'formula', 'latex'],
                category: 'advanced',
                action: () => onCommand({ id: 'equation' } as SlashCommand, blockId),
            },
            // Layout
            {
                id: 'toggle-heading-1',
                label: 'Toggle heading 1',
                description: 'Heading 1 that can be collapsed',
                icon: 'ChevronDown',
                keywords: ['toggle', 'heading', 'h1', 'collapse'],
                category: 'layout',
                action: () => onCommand({ id: 'toggle-heading-1' } as SlashCommand, blockId),
            },
            {
                id: 'toggle-heading-2',
                label: 'Toggle heading 2',
                description: 'Heading 2 that can be collapsed',
                icon: 'ChevronDown',
                keywords: ['toggle', 'heading', 'h2', 'collapse'],
                category: 'layout',
                action: () => onCommand({ id: 'toggle-heading-2' } as SlashCommand, blockId),
            },
            {
                id: 'toggle-heading-3',
                label: 'Toggle heading 3',
                description: 'Heading 3 that can be collapsed',
                icon: 'ChevronDown',
                keywords: ['toggle', 'heading', 'h3', 'collapse'],
                category: 'layout',
                action: () => onCommand({ id: 'toggle-heading-3' } as SlashCommand, blockId),
            },
            {
                id: 'columns-2',
                label: '2 columns',
                description: 'Create a 2-column layout',
                icon: 'Columns2',
                keywords: ['columns', 'layout', '2', 'two'],
                category: 'layout',
                action: () => onCommand({ id: 'columns-2' } as SlashCommand, blockId),
            },
            {
                id: 'columns-3',
                label: '3 columns',
                description: 'Create a 3-column layout',
                icon: 'Columns3',
                keywords: ['columns', 'layout', '3', 'three'],
                category: 'layout',
                action: () => onCommand({ id: 'columns-3' } as SlashCommand, blockId),
            },
            // Diagram
            {
                id: 'diagram',
                label: 'Diagram',
                description: 'Create flowcharts, sequence diagrams with Mermaid',
                icon: 'Network',
                keywords: ['diagram', 'flowchart', 'mermaid', 'chart', 'graph'],
                category: 'media',
                action: () => onCommand({ id: 'diagram' } as SlashCommand, blockId),
            },
            // Templates
            ...BLOCK_TEMPLATES.map(template => ({
                id: `template-${template.id}`,
                label: `${template.icon} ${template.name}`,
                description: template.description,
                icon: 'FileStack',
                keywords: ['template', template.name.toLowerCase(), template.category, ...template.name.toLowerCase().split(' ')],
                category: 'advanced' as const,
                action: () => onCommand({ id: `template-${template.id}`, templateId: template.id } as any, blockId),
            })),
        ];
    }, [onCommand]);

    // Filter commands based on search query
    const filteredCommands = useCallback((blockId: string) => {
        const commands = getCommands(blockId);
        if (!searchQuery) return commands;

        const query = searchQuery.toLowerCase();
        return commands.filter(
            (cmd) =>
                cmd.label.toLowerCase().includes(query) ||
                cmd.description?.toLowerCase().includes(query) ||
                cmd.keywords.some((kw) => kw.toLowerCase().includes(query))
        );
    }, [searchQuery, getCommands]);

    // Open menu
    const openMenu = useCallback((blockId: string, cursorPosition?: { x: number; y: number }) => {
        setCurrentBlockId(blockId);
        setIsOpen(true);
        setSearchQuery('');
        setSelectedIndex(0);
        if (cursorPosition) {
            setPosition(cursorPosition);
        }
    }, []);

    // Close menu
    const closeMenu = useCallback(() => {
        setIsOpen(false);
        setSearchQuery('');
        setSelectedIndex(0);
        setCurrentBlockId(null);
    }, []);

    // Execute selected command
    const executeCommand = useCallback((index?: number) => {
        if (!currentBlockId) return;

        const commands = filteredCommands(currentBlockId);
        const commandIndex = index !== undefined ? index : selectedIndex;
        const command = commands[commandIndex];

        if (command) {
            command.action(currentBlockId);
            closeMenu();
        }
    }, [currentBlockId, selectedIndex, filteredCommands, closeMenu]);

    // Navigate commands with keyboard
    const navigateUp = useCallback(() => {
        setSelectedIndex((prev) => {
            const commands = currentBlockId ? filteredCommands(currentBlockId) : [];
            return prev > 0 ? prev - 1 : commands.length - 1;
        });
    }, [currentBlockId, filteredCommands]);

    const navigateDown = useCallback(() => {
        setSelectedIndex((prev) => {
            const commands = currentBlockId ? filteredCommands(currentBlockId) : [];
            return prev < commands.length - 1 ? prev + 1 : 0;
        });
    }, [currentBlockId, filteredCommands]);

    // Handle keyboard events
    useEffect(() => {
        if (!isOpen) return;

        const handleKeyDown = (e: KeyboardEvent) => {
            switch (e.key) {
                case 'ArrowUp':
                    e.preventDefault();
                    navigateUp();
                    break;
                case 'ArrowDown':
                    e.preventDefault();
                    navigateDown();
                    break;
                case 'Enter':
                    e.preventDefault();
                    executeCommand();
                    break;
                case 'Escape':
                    e.preventDefault();
                    closeMenu();
                    break;
            }
        };

        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, [isOpen, navigateUp, navigateDown, executeCommand, closeMenu]);

    return {
        isOpen,
        openMenu,
        closeMenu,
        searchQuery,
        setSearchQuery,
        selectedIndex,
        setSelectedIndex,
        position,
        currentBlockId,
        commands: currentBlockId ? filteredCommands(currentBlockId) : [],
        executeCommand,
        menuRef,
    };
};

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { toast } from './use-toast';
import {
    listWorkflows,
    listWorkflowRuns,
    getWorkflowRun,
    rerunWorkflow,
    rerunFailedJobs,
    cancelWorkflowRun,
    triggerWorkflow as triggerWorkflowService,
    listWorkflowJobs,
} from '@/services/github/actions.service';
import type { GitHubWorkflow, GitHubWorkflowRun } from '@/types/github';

interface UseWorkflowsProps {
    owner: string;
    repo: string;
    enabled?: boolean;
}

export function useWorkflows({ owner, repo, enabled = true }: UseWorkflowsProps) {
    const queryClient = useQueryClient();

    // Query: List workflows
    const workflows = useQuery({
        queryKey: ['workflows', owner, repo],
        queryFn: () => listWorkflows(owner, repo),
        enabled: enabled && !!owner && !!repo,
        staleTime: 10 * 60 * 1000, // 10 minutes
    });

    // Query: List workflow runs
    const runs = useQuery({
        queryKey: ['workflow-runs', owner, repo],
        queryFn: () => listWorkflowRuns(owner, repo),
        enabled: enabled && !!owner && !!repo,
        staleTime: 1 * 60 * 1000, // 1 minute
        refetchInterval: 30 * 1000, // Auto-refresh every 30s for active runs
    });

    // Mutation: Trigger workflow
    const triggerWorkflowMutation = useMutation({
        mutationFn: ({
            workflowId,
            ref,
            inputs,
        }: {
            workflowId: number | string;
            ref: string;
            inputs?: Record<string, any>;
        }) => triggerWorkflowService(owner, repo, workflowId, ref, inputs),
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['workflow-runs', owner, repo] });
            toast({
                title: 'Workflow triggered',
                description: 'The workflow has been queued for execution',
            });
        },
        onError: (error: Error) => {
            toast({
                title: 'Failed to trigger workflow',
                description: error.message,
                variant: 'destructive',
            });
        },
    });

    // Mutation: Re-run workflow
    const rerunMutation = useMutation({
        mutationFn: (runId: number) => rerunWorkflow(owner, repo, runId),
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['workflow-runs', owner, repo] });
            toast({
                title: 'Workflow re-run started',
                description: 'The workflow is running again',
            });
        },
        onError: (error: Error) => {
            toast({
                title: 'Failed to re-run workflow',
                description: error.message,
                variant: 'destructive',
            });
        },
    });

    // Mutation: Re-run failed jobs
    const rerunFailedMutation = useMutation({
        mutationFn: (runId: number) => rerunFailedJobs(owner, repo, runId),
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['workflow-runs', owner, repo] });
            toast({
                title: 'Re-running failed jobs',
                description: 'Failed jobs are being executed again',
            });
        },
        onError: (error: Error) => {
            toast({
                title: 'Failed to re-run jobs',
                description: error.message,
                variant: 'destructive',
            });
        },
    });

    // Mutation: Cancel workflow
    const cancelMutation = useMutation({
        mutationFn: (runId: number) => cancelWorkflowRun(owner, repo, runId),
        onSuccess: () => {
            queryClient.invalidateQueries({ queryKey: ['workflow-runs', owner, repo] });
            toast({
                title: 'Workflow cancelled',
                description: 'The workflow run has been cancelled',
            });
        },
        onError: (error: Error) => {
            toast({
                title: 'Failed to cancel workflow',
                description: error.message,
                variant: 'destructive',
            });
        },
    });

    return {
        // Queries
        workflows: workflows.data || [],
        runs: runs.data || [],
        isLoadingWorkflows: workflows.isLoading,
        isLoadingRuns: runs.isLoading,
        isLoading: workflows.isLoading || runs.isLoading,

        // Mutations
        triggerWorkflow: triggerWorkflowMutation.mutate,
        isTriggeringWorkflow: triggerWorkflowMutation.isPending,

        rerunWorkflow: rerunMutation.mutate,
        isRerunningWorkflow: rerunMutation.isPending,

        rerunFailedJobs: rerunFailedMutation.mutate,
        isRerunningFailedJobs: rerunFailedMutation.isPending,

        cancelWorkflow: cancelMutation.mutate,
        isCancellingWorkflow: cancelMutation.isPending,

        // Refetch
        refetchWorkflows: workflows.refetch,
        refetchRuns: runs.refetch,
    };
}

/**
 * Hook to get a single workflow run with jobs
 */
export function useWorkflowRun(
    owner: string,
    repo: string,
    runId: number | null,
    enabled = true
) {
    const run = useQuery({
        queryKey: ['workflow-run', owner, repo, runId],
        queryFn: () => (runId ? getWorkflowRun(owner, repo, runId) : Promise.reject('No run ID')),
        enabled: enabled && !!owner && !!repo && !!runId,
        staleTime: 30 * 1000, // 30 seconds
        refetchInterval: (query) => {
            const data = query.state.data;
            // Auto-refresh if run is in progress or queued
            if (data?.status === 'in_progress' || data?.status === 'queued') {
                return 10 * 1000; // 10 seconds
            }
            return false; // Don't auto-refresh completed runs
        },
    });

    const jobs = useQuery({
        queryKey: ['workflow-jobs', owner, repo, runId],
        queryFn: () => (runId ? listWorkflowJobs(owner, repo, runId) : Promise.reject('No run ID')),
        enabled: enabled && !!owner && !!repo && !!runId,
        staleTime: 30 * 1000,
    });

    return {
        run: run.data,
        jobs: jobs.data,
        isLoading: run.isLoading || jobs.isLoading,
        refetch: () => {
            run.refetch();
            jobs.refetch();
        },
    };
}

