interface LanguageSelectorProps {
  value: string;
  onChange: (value: string) => void;
  onCopy: () => void;
  disabled?: boolean;
}

export function LanguageSelector({
  value,
  onChange,
  onCopy,
  disabled = false,
}: LanguageSelectorProps) {
  const [open, setOpen] = useState(false);
  const [copied, setCopied] = useState(false);

  const selectedLanguage = LANGUAGES.find((lang) => lang.value === value);

  const handleCopy = () => {
    onCopy();
    setCopied(true);
    toast({
      description: "Code copied to clipboard",
    });
    setTimeout(() => setCopied(false), 2000);
  };

  // Group languages by category
  const groupedLanguages = LANGUAGES.reduce((acc, lang) => {
    if (!acc[lang.category]) {
      acc[lang.category] = [];
    }
    acc[lang.category].push(lang);
    return acc;
  }, {} as Record<string, typeof LANGUAGES>);

  return (
    <div className="flex items-center gap-2">
      <Popover open={open} onOpenChange={setOpen}>
        <PopoverTrigger asChild>
          <Button
            variant="ghost"
            size="sm"
            role="combobox"
            aria-expanded={open}
            className="h-7 justify-between gap-1 text-xs font-normal"
            disabled={disabled}
          >
            {selectedLanguage?.label || "Select language"}
            <ChevronDown className="h-3 w-3 opacity-50" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-64 p-0" align="start">
          <Command>
            <CommandInput placeholder="Search for a language..." className="h-9" />
            <CommandList className="max-h-[300px]">
              <CommandEmpty>No language found.</CommandEmpty>
              {Object.entries(groupedLanguages).map(([category, langs]) => (
                <CommandGroup key={category} heading={category}>
                  {langs.map((lang) => (
                    <CommandItem
                      key={lang.value}
                      value={lang.label}
                      onSelect={() => {
                        onChange(lang.value);
                        setOpen(false);
                      }}
                      className="text-sm"
                    >
                      {lang.label}
                      <Check
                        className={cn(
                          "ml-auto h-4 w-4",
                          value === lang.value ? "opacity-100" : "opacity-0"
                        )}
                      />
                    </CommandItem>
                  ))}
                </CommandGroup>
              ))}
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>

      <Button
        variant="ghost"
        size="sm"
        onClick={handleCopy}
        className="h-7 px-2 text-xs"
        disabled={disabled}
      >
        {copied ? <Check className="h-3 w-3" /> : <Copy className="h-3 w-3" />}
        <span className="ml-1">{copied ? "Copied" : "Copy"}</span>
      </Button>

      <Button
        variant="ghost"
        size="icon"
        className="h-7 w-7"
        disabled={disabled}
      >
        <MoreHorizontal className="h-3 w-3" />
      </Button>
    </div>
  );
}

import { useState, useEffect, useRef } from 'react';
import { Loader2, AlertCircle } from 'lucide-react';
import mermaid from 'mermaid';
import { cn } from '@/lib/utils';
import { Textarea } from '@/components/ui/textarea';

interface DiagramBlockProps {
    content: string;
    onChange: (content: string) => void;
    onFocus?: () => void;
    isFocused?: boolean;
    readOnly?: boolean;
}

// Initialize Mermaid
mermaid.initialize({
    startOnLoad: true,
    theme: 'default',
    securityLevel: 'loose',
    fontFamily: 'inherit',
});

export function DiagramBlock({
    content,
    onChange,
    onFocus,
    isFocused,
    readOnly
}: DiagramBlockProps) {
    const [isEditing, setIsEditing] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [isRendering, setIsRendering] = useState(false);
    const diagramRef = useRef<HTMLDivElement>(null);
    const renderIdRef = useRef(0);

    useEffect(() => {
        if (!isEditing && content && diagramRef.current) {
            renderDiagram();
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [content, isEditing]);

    const renderDiagram = async () => {
        if (!diagramRef.current || !content.trim()) return;

        setIsRendering(true);
        setError(null);

        try {
            const id = `mermaid-${Date.now()}-${renderIdRef.current++}`;
            const { svg } = await mermaid.render(id, content);

            if (diagramRef.current) {
                diagramRef.current.innerHTML = svg;
            }
        } catch (err: unknown) {
            const errorMessage = err instanceof Error ? err.message : 'Failed to render diagram';
            setError(errorMessage);
            console.error('Mermaid render error:', err);
        } finally {
            setIsRendering(false);
        }
    };

    const handleBlur = () => {
        setIsEditing(false);
        renderDiagram();
    };

    if (readOnly || (!isEditing && content)) {
        return (
            <div
                className={cn(
                    "my-2 p-4 rounded-lg border border-border/50 bg-muted/30 cursor-pointer",
                    isFocused && "ring-2 ring-primary/20"
                )}
                onClick={() => !readOnly && setIsEditing(true)}
                onFocus={onFocus}
                tabIndex={0}
            >
                {error ? (
                    <div className="flex items-center gap-2 text-destructive">
                        <AlertCircle className="h-4 w-4" />
                        <span className="text-sm">{error}</span>
                    </div>
                ) : isRendering ? (
                    <div className="flex items-center justify-center py-8">
                        <Loader2 className="h-6 w-6 animate-spin text-muted-foreground" />
                    </div>
                ) : (
                    <div
                        ref={diagramRef}
                        className="mermaid-diagram flex items-center justify-center"
                        style={{ minHeight: '100px' }}
                    />
                )}

                {!readOnly && (
                    <p className="text-xs text-muted-foreground mt-2 text-center">
                        Click to edit diagram
                    </p>
                )}
            </div>
        );
    }

    return (
        <div className="my-2 space-y-2">
            <Textarea
                value={content}
                onChange={(e) => onChange(e.target.value)}
                onBlur={handleBlur}
                onFocus={onFocus}
                placeholder="Enter Mermaid diagram code...&#10;&#10;Example:&#10;graph TD&#10;  A[Start] --> B{Decision}&#10;  B -->|Yes| C[Result]"
                className="font-mono text-sm min-h-[150px]"
                autoFocus
            />
            <div className="flex items-center gap-2">
                <p className="text-xs text-muted-foreground">
                    Using Mermaid syntax. <a
                        href="https://mermaid.js.org/intro/"
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-primary hover:underline"
                    >
                        See docs
                    </a>
                </p>
            </div>
        </div>
    );
}

