/**
 * CodeEditor Component - Monaco Editor Integration
 * 
 * Features:
 * - Syntax highlighting for all languages
 * - IntelliSense and autocomplete
 * - Keyboard shortcuts (Cmd+S, Cmd+F, etc.)
 * - Theme support (light/dark)
 * - Diff view support
 */

import { useEffect, useRef, useState } from 'react';
import Editor, { OnMount, Monaco } from '@monaco-editor/react';
import { useTheme } from "@/hooks/use-theme";
import { Skeleton } from '@/components/ui/skeleton';
import type { editor } from 'monaco-editor';

interface CodeEditorProps {
    value: string;
    language?: string;
    onChange?: (value: string | undefined) => void;
    onSave?: (value: string) => void;
    readOnly?: boolean;
    height?: string;
    options?: editor.IStandaloneEditorConstructionOptions;
}

export function CodeEditor({
    value,
    language = 'javascript',
    onChange,
    onSave,
    readOnly = false,
    height = '100%',
    options = {},
}: CodeEditorProps) {
    const { theme } = useTheme();
    const editorRef = useRef<editor.IStandaloneCodeEditor | null>(null);
    const monacoRef = useRef<Monaco | null>(null);
    const [isReady, setIsReady] = useState(false);

    const handleEditorDidMount: OnMount = (editor, monaco) => {
        editorRef.current = editor;
        monacoRef.current = monaco;
        setIsReady(true);

        // Add keyboard shortcuts
        editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyS, () => {
            if (onSave) {
                const currentValue = editor.getValue();
                onSave(currentValue);
            }
        });

        // Focus editor
        editor.focus();
    };

    const handleChange = (value: string | undefined) => {
        if (onChange) {
            onChange(value);
        }
    };

    // Configure editor options
    const editorOptions: editor.IStandaloneEditorConstructionOptions = {
        minimap: { enabled: true },
        fontSize: 14,
        lineNumbers: 'on',
        roundedSelection: true,
        scrollBeyondLastLine: false,
        readOnly,
        automaticLayout: true,
        tabSize: 2,
        wordWrap: 'on',
        formatOnPaste: true,
        formatOnType: true,
        suggestOnTriggerCharacters: true,
        acceptSuggestionOnEnter: 'on',
        quickSuggestions: true,
        parameterHints: { enabled: true },
        ...options,
    };

    return (
        <div className="w-full h-full relative">
            <Editor
                height={height}
                language={language}
                value={value}
                onChange={handleChange}
                onMount={handleEditorDidMount}
                theme={theme === 'dark' ? 'vs-dark' : 'light'}
                options={editorOptions}
                loading={
                    <div className="w-full h-full p-4">
                        <Skeleton className="w-full h-8 mb-2" />
                        <Skeleton className="w-3/4 h-8 mb-2" />
                        <Skeleton className="w-full h-8 mb-2" />
                        <Skeleton className="w-2/3 h-8 mb-2" />
                        <Skeleton className="w-full h-8 mb-2" />
                    </div>
                }
            />
        </div>
    );
}

// Language detection from file extension
export function getLanguageFromFilename(filename: string): string {
    const ext = filename.split('.').pop()?.toLowerCase();

    const languageMap: Record<string, string> = {
        // Web
        js: 'javascript',
        jsx: 'javascript',
        ts: 'typescript',
        tsx: 'typescript',
        html: 'html',
        css: 'css',
        scss: 'scss',
        less: 'less',
        json: 'json',

        // Backend
        py: 'python',
        java: 'java',
        go: 'go',
        rs: 'rust',
        php: 'php',
        rb: 'ruby',

        // Shell
        sh: 'shell',
        bash: 'shell',
        zsh: 'shell',

        // Config
        yaml: 'yaml',
        yml: 'yaml',
        toml: 'toml',
        xml: 'xml',

        // Docs
        md: 'markdown',
        txt: 'plaintext',

        // Database
        sql: 'sql',

        // Others
        c: 'c',
        cpp: 'cpp',
        cs: 'csharp',
        swift: 'swift',
        kt: 'kotlin',
        dart: 'dart',
    };

    return languageMap[ext || ''] || 'plaintext';
}

/**
 * FileTree Component - Repository file browser
 * 
 * Browse and select files from a repository
 */

import { useState } from 'react';
import { ChevronRight, ChevronDown, File, Folder, FolderOpen } from 'lucide-react';
import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';
import { ScrollArea } from '@/components/ui/scroll-area';

export interface FileNode {
    path: string;
    name: string;
    type: 'file' | 'dir';
    children?: FileNode[];
    size?: number;
}

interface FileTreeProps {
    files: FileNode[];
    onFileSelect: (file: FileNode) => void;
    selectedPath?: string;
}

export function FileTree({ files, onFileSelect, selectedPath }: FileTreeProps) {
    return (
        <ScrollArea className="h-full">
            <div className="p-2">
                {files.map((node) => (
                    <FileTreeNode
                        key={node.path}
                        node={node}
                        level={0}
                        onSelect={onFileSelect}
                        selectedPath={selectedPath}
                    />
                ))}
            </div>
        </ScrollArea>
    );
}

interface FileTreeNodeProps {
    node: FileNode;
    level: number;
    onSelect: (file: FileNode) => void;
    selectedPath?: string;
}

function FileTreeNode({ node, level, onSelect, selectedPath }: FileTreeNodeProps) {
    const [isExpanded, setIsExpanded] = useState(level === 0);
    const isSelected = selectedPath === node.path;
    const isDirectory = node.type === 'dir';

    const handleClick = () => {
        if (isDirectory) {
            setIsExpanded(!isExpanded);
        } else {
            onSelect(node);
        }
    };

    return (
        <div>
            <Button
                variant="ghost"
                className={cn(
                    'w-full justify-start px-2 py-1 h-auto font-normal hover:bg-accent',
                    isSelected && 'bg-accent'
                )}
                style={{ paddingLeft: `${level * 12 + 8}px` }}
                onClick={handleClick}
            >
                {isDirectory ? (
                    <>
                        {isExpanded ? (
                            <ChevronDown className="h-4 w-4 mr-1 shrink-0" />
                        ) : (
                            <ChevronRight className="h-4 w-4 mr-1 shrink-0" />
                        )}
                        {isExpanded ? (
                            <FolderOpen className="h-4 w-4 mr-2 shrink-0 text-blue-500" />
                        ) : (
                            <Folder className="h-4 w-4 mr-2 shrink-0 text-blue-500" />
                        )}
                    </>
                ) : (
                    <>
                        <span className="w-4 mr-1" /> {/* Spacer for alignment */}
                        <File className="h-4 w-4 mr-2 shrink-0 text-muted-foreground" />
                    </>
                )}
                <span className="truncate text-sm">{node.name}</span>
            </Button>

            {isDirectory && isExpanded && node.children && (
                <div>
                    {node.children.map((child) => (
                        <FileTreeNode
                            key={child.path}
                            node={child}
                            level={level + 1}
                            onSelect={onSelect}
                            selectedPath={selectedPath}
                        />
                    ))}
                </div>
            )}
        </div>
    );
}

/**
 * Convert flat GitHub tree structure to nested file tree
 */
export function buildFileTree(files: Array<{ path: string; type: 'blob' | 'tree' }>): FileNode[] {
    const root: FileNode[] = [];
    const pathMap = new Map<string, FileNode>();

    // Sort files by path to ensure parents come before children
    const sortedFiles = [...files].sort((a, b) => a.path.localeCompare(b.path));

    for (const file of sortedFiles) {
        const parts = file.path.split('/');
        const name = parts[parts.length - 1];

        const node: FileNode = {
            path: file.path,
            name,
            type: file.type === 'tree' ? 'dir' : 'file',
            children: file.type === 'tree' ? [] : undefined,
        };

        pathMap.set(file.path, node);

        if (parts.length === 1) {
            // Root level
            root.push(node);
        } else {
            // Nested - find parent
            const parentPath = parts.slice(0, -1).join('/');
            const parent = pathMap.get(parentPath);

            if (parent && parent.type === 'dir') {
                parent.children = parent.children || [];
                parent.children.push(node);
            } else {
                // Parent doesn't exist yet, create placeholder directories
                let currentPath = '';
                for (let i = 0; i < parts.length - 1; i++) {
                    const part = parts[i];
                    const newPath = currentPath ? `${currentPath}/${part}` : part;

                    if (!pathMap.has(newPath)) {
                        const dirNode: FileNode = {
                            path: newPath,
                            name: part,
                            type: 'dir',
                            children: [],
                        };

                        pathMap.set(newPath, dirNode);

                        if (i === 0) {
                            root.push(dirNode);
                        } else {
                            const parentDir = pathMap.get(currentPath);
                            if (parentDir && parentDir.children) {
                                parentDir.children.push(dirNode);
                            }
                        }
                    }

                    currentPath = newPath;
                }

                // Now add the file to its parent
                const finalParent = pathMap.get(parts.slice(0, -1).join('/'));
                if (finalParent && finalParent.children) {
                    finalParent.children.push(node);
                }
            }
        }
    }

    return root;
}

import { useState } from 'react';
import { Calendar as CalendarIcon, ChevronLeft, ChevronRight } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Calendar } from '@/components/ui/calendar';
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/components/ui/popover';
import { format, subDays, addDays } from 'date-fns';

interface DailyNotesProps {
    onSelectDate: (date: Date) => void;
    currentDate?: Date;
}

export function DailyNotes({ onSelectDate, currentDate = new Date() }: DailyNotesProps) {
    const [date, setDate] = useState<Date>(currentDate);

    const handleDateSelect = (newDate: Date | undefined) => {
        if (newDate) {
            setDate(newDate);
            onSelectDate(newDate);
        }
    };

    const goToPrevDay = () => {
        const prev = subDays(date, 1);
        setDate(prev);
        onSelectDate(prev);
    };

    const goToNextDay = () => {
        const next = addDays(date, 1);
        setDate(next);
        onSelectDate(next);
    };

    const goToToday = () => {
        const today = new Date();
        setDate(today);
        onSelectDate(today);
    };

    const isToday = format(date, 'yyyy-MM-dd') === format(new Date(), 'yyyy-MM-dd');

    return (
        <div className="flex items-center gap-2">
            <Button
                variant="outline"
                size="icon"
                onClick={goToPrevDay}
                title="Previous day"
            >
                <ChevronLeft className="h-4 w-4" />
            </Button>

            <Popover>
                <PopoverTrigger asChild>
                    <Button variant="outline" className="min-w-[200px] justify-start">
                        <CalendarIcon className="mr-2 h-4 w-4" />
                        {format(date, 'EEEE, MMM d, yyyy')}
                    </Button>
                </PopoverTrigger>
                <PopoverContent className="w-auto p-0" align="start">
                    <Calendar
                        mode="single"
                        selected={date}
                        onSelect={handleDateSelect}
                        initialFocus
                    />
                </PopoverContent>
            </Popover>

            <Button
                variant="outline"
                size="icon"
                onClick={goToNextDay}
                title="Next day"
            >
                <ChevronRight className="h-4 w-4" />
            </Button>

            {!isToday && (
                <Button variant="ghost" onClick={goToToday}>
                    Today
                </Button>
            )}
        </div>
    );
}

